import "./App.css";
import { useContext } from "react";
// atraves do UseContext acessaremos o nosso contexto

import { CounterContext } from "./Providers/counter";

// importaremos o contexto a ser utilizado , e passamos esse contexto para o useContext

function App() {
  const { counter, addNumber, subNumber } = useContext(CounterContext);

  return (
    <div className="App">
      <header className="App-header">
        {counter}
        <button onClick={addNumber}> + 1</button>
        <button onClick={subNumber}> - 1 </button>
      </header>
    </div>
  );
}

export default App;
