import styled from "styled-components";

export const Container = styled.div`
  background-color: red;
  width: 400px;
  height: 60px;

  ul {
    background-color: black;
    height: 50px;
    margin: auto;
  }
`;

export const List = styled.div`
  height: 40px;
`;
